import { Component } from '@angular/core';

@Component({
  selector: 'app-directives',
  templateUrl: './directives.component.html',
  styleUrls: ['./directives.component.css']
})
export class DirectivesComponent {

  font = 'Arial';
  color = 'blue';
  classes = ["green-title", "small-title"]
}
<!--Criando uma diretiva com ng...-->
<h2 [ngStyle]="{ 'font-size':'16px', 'font-family': font, 'color': color }">Testando diretiva de estilos / style</h2>
<h2 [ngClass]="classes">Testando diretrizes de classe 1</h2>
