import { Component } from '@angular/core';

@Component({
  selector: 'app-first-component',
  templateUrl: './first-component.component.html',
  styleUrls: ['./first-component.component.css']
})
export class FirstComponentComponent {

  name = "Caique"
  age = 26;
  job = "Programador"
  hobbies = ["Correr ", "Jogar ", "Estudar"]
  car = {
    name: "Duster",
    year: 2017

  }
}
<div class="global"></div>
<h2>Nome: {{ name }}</h2>
<p>Idade: {{ age }}</p>
<p>Trabalho: {{ job }}</p>
<p>Hobbies que mais gostam: {{ hobbies }}</p>
<p>O nome do carro é: {{ car.name }}</p>
<p>O ano de fabricação do carro é: {{ car.year }}</p>
<div>
    <a href="#">Testando link do css componente</a>
</div>
